db/schema.ts.content:
import { pgTable, text, serial, timestamp, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema, createSelectSchema } from "drizzle-zod";

export const rooms = pgTable("rooms", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const requests = pgTable("requests", {
  id: serial("id").primaryKey(),
  roomId: serial("room_id").references(() => rooms.id),
  musician: text("musician").notNull(),
  instrument: text("instrument").notNull(),
  targetInstrument: text("target_instrument").notNull(),
  action: text("action").notNull(), // "volume_up", "volume_down", "reverb_up", "reverb_down"
  createdAt: timestamp("created_at").defaultNow().notNull(),
  completed: boolean("completed").default(false),
});

export const customInstruments = pgTable("custom_instruments", {
  id: serial("id").primaryKey(),
  roomId: serial("room_id").references(() => rooms.id),
  name: text("name").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export type Room = typeof rooms.$inferSelect;
export type Request = typeof requests.$inferSelect;
export type CustomInstrument = typeof customInstruments.$inferSelect;

export const insertRoomSchema = createInsertSchema(rooms);
export const selectRoomSchema = createSelectSchema(rooms);
export const insertRequestSchema = createInsertSchema(requests);
export const selectRequestSchema = createSelectSchema(requests);
export const insertCustomInstrumentSchema = createInsertSchema(customInstruments);
export const selectCustomInstrumentSchema = createSelectSchema(customInstruments);

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { setupVite, serveStatic, log } from "./vite";
import { registerRoutes } from "./routes";

const app = express();

// Configuración básica de Express
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// Middleware de logging
app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

// Error handling middleware
app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
  console.error("[Error]", err);
  const errorMessage = app.get("env") === "development" ? err.message : "Error interno del servidor";
  res.status(err.status || 500).json({ message: errorMessage });
});

// Función para limpiar recursos del servidor
async function cleanupServer(server: Server) {
  return new Promise<void>((resolve) => {
    server.close(() => {
      log("[Server] Servidor HTTP cerrado");
      resolve();
    });
  });
}

// Función para crear y configurar el servidor
async function createAndConfigureServer() {
  try {
    // Crear servidor HTTP y registrar rutas (WebSocket ya está configurado dentro de registerRoutes)
    const server = registerRoutes(app);
    log("[Server] Servidor HTTP y WebSocket creados");

    // Setup Vite o archivos estáticos
    if (app.get("env") === "development") {
      await setupVite(app, server);
      log("[Server] Vite configurado en modo desarrollo");
    } else {
      serveStatic(app);
      log("[Server] Archivos estáticos configurados");
    }

    return server;
  } catch (error) {
    log("[Server] Error en la configuración del servidor");
    throw error;
  }
}

// Inicialización del servidor con reintento de puertos
async function startServer(initialPort: number, maxRetries: number = 5) {
  let currentPort = initialPort;
  let attempts = 0;
  let server: Server | null = null;

  while (attempts < maxRetries) {
    try {
      log(`[Server] Intento ${attempts + 1} de ${maxRetries} en puerto ${currentPort}`);

      // Limpiar servidor anterior si existe
      if (server) {
        await cleanupServer(server);
        server = null;
      }

      // Crear y configurar nuevo servidor
      server = await createAndConfigureServer();

      // Intentar iniciar el servidor
      await new Promise<void>((resolve, reject) => {
        if (!server) {
          reject(new Error("No se pudo crear el servidor"));
          return;
        }

        const errorHandler = (error: Error) => {
          server?.removeListener('error', errorHandler);
          reject(error);
        };

        server.once('error', errorHandler);

        server.listen(currentPort, "0.0.0.0", () => {
          server?.removeListener('error', errorHandler);
          log(`[Server] Servidor iniciado exitosamente en puerto ${currentPort}`);
          resolve();
        });
      });

      return; // Servidor iniciado correctamente
    } catch (error: any) {
      attempts++;

      if (error.code === 'EADDRINUSE') {
        log(`[Server] Puerto ${currentPort} en uso, intentando puerto ${currentPort + 1}...`);
        currentPort++;
        await new Promise(resolve => setTimeout(resolve, 1000));
      } else {
        console.error(`[Server] Error inesperado:`, error);
        if (attempts === maxRetries) {
          if (server) {
            await cleanupServer(server);
          }
          throw new Error(`No se pudo iniciar el servidor después de ${maxRetries} intentos: ${error.message}`);
        }
      }
    }
  }
}

// Iniciar el servidor
startServer(5000).catch(error => {
  console.error("[Server] Error fatal:", error);
  process.exit(1);
});

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { db } from "@db";
import { rooms, requests, customInstruments } from "@db/schema";
import { eq, and } from "drizzle-orm";
import { requestSchema, RequestData } from "./websocket";

export function registerRoutes(app: Express): Server {
  // API Routes
  // Buscar o crear sala
  app.post("/api/rooms/find-or-create", async (req, res, next) => {
    try {
      const roomName = req.body.name || 'Sala sin nombre';

      // Primero buscar si ya existe una sala con ese nombre
      const existingRoom = await db.query.rooms.findFirst({
        where: eq(rooms.name, roomName)
      });

      if (existingRoom) {
        console.log(`[API] Sala encontrada: ${existingRoom.name} (ID: ${existingRoom.id})`);
        res.json({ ...existingRoom, isExisting: true });
        return;
      }

      // Si no existe, crear nueva sala
      const [room] = await db.insert(rooms)
        .values({ name: roomName })
        .returning();
      console.log(`[API] Nueva sala creada: ${room.name} (ID: ${room.id})`);
      res.json({ ...room, isExisting: false });
    } catch (error) {
      console.error("[API] Error buscando/creando sala:", error);
      next(error);
    }
  });

  // Crear sala nueva (forzar)
  app.post("/api/rooms", async (req, res, next) => {
    try {
      const [room] = await db.insert(rooms)
        .values({ name: req.body.name || 'Sala sin nombre' })
        .returning();
      res.json(room);
    } catch (error) {
      console.error("[API] Error creando sala:", error);
      next(error);
    }
  });

  // Buscar salas por nombre
  app.get("/api/rooms/search", async (req, res, next) => {
    try {
      const name = req.query.name as string;
      if (!name) {
        res.status(400).json({ message: "Nombre requerido" });
        return;
      }

      const room = await db.query.rooms.findFirst({
        where: eq(rooms.name, name)
      });

      if (room) {
        res.json(room);
      } else {
        res.status(404).json({ message: "Sala no encontrada" });
      }
    } catch (error) {
      console.error("[API] Error buscando sala:", error);
      next(error);
    }
  });

  // Obtener instrumentos personalizados de una sala
  app.get("/api/rooms/:roomId/instruments", async (req, res, next) => {
    try {
      const { roomId } = req.params;
      const roomIdNum = parseInt(roomId, 10);

      if (isNaN(roomIdNum)) {
        res.status(400).json({ message: "ID de sala inválido" });
        return;
      }

      const instruments = await db.query.customInstruments.findMany({
        where: eq(customInstruments.roomId, roomIdNum)
      });

      res.json(instruments);
    } catch (error) {
      console.error("[API] Error obteniendo instrumentos:", error);
      next(error);
    }
  });

  // Crear un instrumento personalizado en una sala
  app.post("/api/rooms/:roomId/instruments", async (req, res, next) => {
    try {
      const { roomId } = req.params;
      const roomIdNum = parseInt(roomId, 10);

      if (isNaN(roomIdNum)) {
        res.status(400).json({ message: "ID de sala inválido" });
        return;
      }

      const { name } = req.body;
      if (!name || !name.trim()) {
        res.status(400).json({ message: "Nombre de instrumento requerido" });
        return;
      }

      // Verificar si ya existe un instrumento con ese nombre en esta sala
      const existing = await db.query.customInstruments.findFirst({
        where: and(
          eq(customInstruments.roomId, roomIdNum),
          eq(customInstruments.name, name.trim())
        )
      });

      if (existing) {
        res.json(existing); // Si ya existe, devolverlo
        return;
      }

      // Crear nuevo instrumento
      const [instrument] = await db.insert(customInstruments)
        .values({ roomId: roomIdNum, name: name.trim() })
        .returning();

      // Propagar a todos los clientes de la sala via WebSocket
      if (wss) {
        broadcastToRoom(roomId, JSON.stringify({
          type: 'newInstrument',
          data: instrument
        }));
      }

      res.json(instrument);
    } catch (error) {
      console.error("[API] Error creando instrumento:", error);
      next(error);
    }
  });

  app.post("/api/rooms/:roomId/requests/:requestId/complete", async (req, res, next) => {
    try {
      const { roomId, requestId } = req.params;
      const requestIdNum = parseInt(requestId, 10);

      if (isNaN(requestIdNum)) {
        res.status(400).json({ message: "ID de petición inválido" });
        return;
      }

      const [updatedRequest] = await db.update(requests)
        .set({ completed: true })
        .where(eq(requests.id, requestIdNum))
        .returning();

      // Send WebSocket notification to all clients in the room
      if (wss && updatedRequest) {
        broadcastToRoom(roomId, JSON.stringify({
          type: 'requestCompleted',
          data: updatedRequest
        }));
      }

      res.json({ message: "Petición completada" });
    } catch (error) {
      console.error("[API] Error completando petición:", error);
      next(error);
    }
  });

  // Add a clear all requests endpoint
  app.post("/api/rooms/:roomId/requests/clear", async (req, res, next) => {
    try {
      const { roomId } = req.params;
      const roomIdNum = parseInt(roomId, 10);

      if (isNaN(roomIdNum)) {
        res.status(400).json({ message: "ID de sala inválido" });
        return;
      }

      const updatedRequests = await db.update(requests)
        .set({ completed: true })
        .where(eq(requests.roomId, roomIdNum))
        .returning();

      // Send WebSocket notification to all clients in the room
      if (wss) {
        broadcastToRoom(roomId, JSON.stringify({
          type: 'allRequestsCompleted',
          roomId: roomId
        }));
      }

      res.json({ message: "Todas las peticiones completadas", count: updatedRequests ? updatedRequests.length : 0 });
    } catch (error) {
      console.error("[API] Error limpiando peticiones:", error);
      next(error);
    }
  });

  const server = createServer(app);

  // Manejar errores del servidor HTTP
  server.on('error', (error: Error) => {
    console.error('[Server] Error en servidor HTTP:', error);
  });

  // Setup WebSocket server on a distinct path
  const wss = new WebSocketServer({ 
    server: server, 
    path: '/ws',
    perMessageDeflate: false,
    maxPayload: 16 * 1024 * 1024,
    clientTracking: true,
    verifyClient: (info: any) => {
      console.log('[WebSocket] Verificando cliente:', info.origin, info.req.url);
      return true; // Accept all connections for now
    }
  });

  console.log('[WebSocket] Servidor WebSocket configurado en path /ws');

  // Handle WebSocket server errors
  wss.on('error', (error) => {
    console.error('[WebSocket] Error en servidor WebSocket:', error);
  });

  // Track clients by room
  const roomClients: Map<string, Set<WebSocket>> = new Map();

  // Broadcast to all clients in a room
  function broadcastToRoom(roomId: string, message: string) {
    const roomSet = roomClients.get(roomId);
    if (roomSet) {
      roomSet.forEach((client) => {
        if (client.readyState === WebSocket.OPEN) {
          client.send(message);
        }
      });
    }
  }

  wss.on('connection', async (ws, req) => {
    console.log('[WebSocket] Cliente conectado');
    console.log('[WebSocket] Request URL:', req.url);
    console.log('[WebSocket] Request headers:', req.headers);

    // Parse the URL to get the roomId
    const url = new URL(req.url || '', `http://${req.headers.host}`);
    const roomId = url.searchParams.get('roomId');

    console.log('[WebSocket] Room ID extraído:', roomId);

    if (!roomId) {
      console.log('[WebSocket] Cliente sin roomId');
      ws.close(1008, 'RoomId es obligatorio');
      return;
    }

    try {
      const roomIdNum = parseInt(roomId, 10);
      if (isNaN(roomIdNum)) {
        throw new Error("RoomId debe ser un número");
      }

      // Check if room exists, if not create it
      let room = await db.query.rooms.findFirst({
        where: eq(rooms.id, roomIdNum)
      });

      if (!room) {
        console.log(`[WebSocket] Sala ${roomId} no encontrada, creando nueva sala`);
        [room] = await db.insert(rooms)
          .values({ name: `Sala ${roomId}` })
          .returning();
        console.log(`[WebSocket] Nueva sala creada: ${room.name}`);
      }

      // Add client to room
      if (!roomClients.has(roomId)) {
        roomClients.set(roomId, new Set());
      }
      roomClients.get(roomId)?.add(ws);

      // Send initial data
      const pendingRequests = await db.query.requests.findMany({
        where: eq(requests.roomId, roomIdNum)
      });

      // Filter only non-completed requests
      const activeRequests = pendingRequests.filter(req => !req.completed);

      console.log(`[WebSocket] Enviando ${activeRequests.length} peticiones activas de ${pendingRequests.length} totales`);

      ws.send(JSON.stringify({
        type: 'initialRequests',
        data: activeRequests
      }));

      // Handle messages from client
      ws.on('message', async (message) => {
        try {
          const data = JSON.parse(message.toString());

          if (data.type === 'request') {
            // Validar datos de la petición
            const validatedData = requestSchema.parse(data.data);
            const roomIdNum = parseInt(validatedData.roomId, 10);

            const [newRequest] = await db.insert(requests)
              .values({
                roomId: roomIdNum,
                musician: validatedData.musician,
                instrument: validatedData.instrument,
                targetInstrument: validatedData.targetInstrument,
                action: validatedData.action,
              })
              .returning();

            // Broadcast to all clients in the room
            broadcastToRoom(roomId, JSON.stringify({
              type: 'newRequest',
              data: newRequest
            }));
          }
        } catch (error) {
          console.error('[WebSocket] Error procesando mensaje:', error);
        }
      });

      // Handle disconnection
      ws.on('close', () => {
        console.log('[WebSocket] Cliente desconectado');
        roomClients.get(roomId)?.delete(ws);
        if (roomClients.get(roomId)?.size === 0) {
          roomClients.delete(roomId);
        }
      });

    } catch (error) {
      console.error('[WebSocket] Error:', error);
      ws.close(1011, 'Error interno');
    }
  });

  return server;
}

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path, { dirname } from "path";
import { fileURLToPath } from "url";
import { createServer as createViteServer, createLogger } from "vite";
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
import { type Server } from "http";
import viteConfig from "../vite.config";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        if (
          msg.includes("[TypeScript] Found 0 errors. Watching for file changes")
        ) {
          log("no errors found", "tsc");
          return;
        }

        if (msg.includes("[TypeScript] ")) {
          const [errors, summary] = msg.split("[TypeScript] ", 2);
          log(`${summary} ${errors}\u001b[0m`, "tsc");
          return;
        } else {
          viteLogger.error(msg, options);
          process.exit(1);
        }
      },
    },
    server: {
      middlewareMode: true,
      hmr: { server },
    },
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        __dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      const template = await fs.promises.readFile(clientTemplate, "utf-8");
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(__dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

