import { useState } from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/hooks/use-language";
import { useLocation } from "wouter";

interface Props {
  onJoin: (roomId: string) => void;
  role: "musician" | "technician";
}

export default function JoinRoomForm({ onJoin, role }: Props) {
  const [roomName, setRoomName] = useState("");
  const [loading, setLoading] = useState(false);
  const [action, setAction] = useState<'join' | 'create' | null>(null);
  const { toast } = useToast();
  const { t } = useLanguage();
  const [, setLocation] = useLocation();

  const handleAction = async (actionType: 'join' | 'create') => {
    if (!roomName.trim()) {
      toast({
        title: t('error'),
        description: t('enterRoomName'),
        variant: "destructive",
      });
      return;
    }

    setAction(actionType);
    setLoading(true);

    try {
      let response;

      if (actionType === 'join') {
        // Intentar unirse a sala existente
        response = await fetch(`/api/rooms/search?name=${encodeURIComponent(roomName)}`);

        if (!response.ok) {
          if (response.status === 404) {
            toast({
              title: t('roomNotFound'),
              description: t('roomNotFoundDesc').replace('{name}', roomName),
              variant: "destructive",
            });
            return;
          }
          throw new Error('Error buscando la sala');
        }
      } else {
        // Crear nueva sala o buscar existente
        response = await fetch('/api/rooms/find-or-create', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ name: roomName })
        });

        if (!response.ok) {
          const error = await response.text();
          throw new Error(error);
        }
      }

      const room = await response.json();

      // Mostrar mensaje informativo
      if (actionType === 'create' && room.isExisting) {
        toast({
          title: t('joinedExistingRoom'),
          description: t('joinedExistingRoomDesc').replace('{name}', roomName),
          duration: 3000,
        });
      } else if (actionType === 'create' && !room.isExisting) {
        toast({
          title: t('roomCreated'),
          description: t('roomCreatedDesc').replace('{name}', roomName),
          duration: 2000,
        });
      } else {
        toast({
          title: t('connected'),
          description: t('connectedDesc').replace('{name}', roomName),
          duration: 2000,
        });
      }

      setLocation(`/${role}/${room.id}`);
    } catch (error) {
      console.error('Error con la sala:', error);
      toast({
        title: t('error'),
        description: error instanceof Error ? error.message : t('errorProcessingRequest'),
        variant: "destructive",
      });
    } finally {
      setLoading(false);
      setAction(null);
    }
  };

  return (
    <div className="space-y-4">
      <Input
        type="text"
        placeholder={t('roomNamePlaceholder')}
        value={roomName}
        onChange={(e) => setRoomName(e.target.value)}
        disabled={loading}
      />

      <div className="grid grid-cols-2 gap-3">
        <Button 
          variant="outline" 
          onClick={() => handleAction('join')}
          disabled={loading || !roomName.trim()}
          className="w-full"
        >
          {loading && action === 'join' ? t('searching') : t('joinRoom')}
        </Button>

        <Button 
          onClick={() => handleAction('create')}
          disabled={loading || !roomName.trim()}
          className="w-full"
        >
          {loading && action === 'create' ? t('creating') : t('createRoom')}
        </Button>
      </div>

      <p className="text-sm text-gray-500 text-center font-light">
        <strong>{t('joinRoom')}:</strong> {t('joinInfo')}<br />
        <strong>{t('createRoom')}:</strong> {t('createInfo')}
      </p>
    </div>
  );
}

client/src/components/RequestForm.tsx.content:
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/hooks/use-language";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { INSTRUMENTS, getInstrumentColor } from "@/lib/constants";

interface Props {
  currentInstrument: string;
  onInstrumentSelect: (instrument: string) => void;
  onRequest: (request: { targetInstrument: string; action: string }) => void;
  roomId: string;
  customInstruments: string[];
}

export default function RequestForm({ currentInstrument, onInstrumentSelect, onRequest, roomId, customInstruments }: Props) {
  const [targetInstrument, setTargetInstrument] = useState("");
  const [loading, setLoading] = useState(false);
  const [showCustomInput, setShowCustomInput] = useState(false);
  const [customInstrument, setCustomInstrument] = useState("");
  const { toast } = useToast();
  const { t } = useLanguage();

  const getActionText = (action: string): string => {
    switch (action) {
      case "volume_up":
        return t('volumeUpAction');
      case "volume_down":
        return t('volumeDownAction');
      case "reverb_up":
        return t('reverbUpAction');
      case "reverb_down":
        return t('reverbDownAction');
      case "thanks":
        return t('thanksAction');
      case "assistance":
        return t('assistanceAction');
      default:
        return action;
    }
  };

  const handleRequest = async (request: {
    targetInstrument: string;
    action: string;
  }) => {
    setLoading(true);
    try {
      onRequest(request);
      toast({
        title: t('requestSent'),
        description: `${t('requestSentDesc')} ${getActionText(request.action)} de ${request.targetInstrument}`,
        duration: 2000,
        className: "w-auto"
      });
    } catch (error) {
      toast({
        title: t('error'),
        description: t('errorSendingRequest'),
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleCustomInstrumentSubmit = async () => {
    const instrumentName = customInstrument.trim();
    if (!instrumentName) return;

    try {
      // Guardar en la base de datos para compartir con todos los usuarios
      const response = await fetch(`/api/rooms/${roomId}/instruments`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: instrumentName })
      });

      if (!response.ok) {
        throw new Error(`Error ${response.status}: ${await response.text()}`);
      }

      // Solo actualizar si el guardado fue exitoso
      onInstrumentSelect(instrumentName);
      setCustomInstrument("");
      setShowCustomInput(false);

      toast({
        title: t('instrumentCreated'),
        description: `${instrumentName} ${t('instrumentAvailable')}`,
        duration: 2000,
      });
    } catch (error) {
      console.error('Error guardando instrumento:', error);
      toast({
        title: t('error'),
        description: t('errorSavingInstrument'),
        variant: "destructive",
      });
    }
  };

  if (!currentInstrument) {
    // Combinar instrumentos predefinidos y personalizados para el select inicial
    const allAvailableInstruments = [...INSTRUMENTS, ...customInstruments.filter(ci => !INSTRUMENTS.includes(ci as any))];

    return (
      <div className="space-y-4">
        <h2 className="text-lg font-light">{t('selectInstrument')}</h2>

        {!showCustomInput ? (
          <>
            <Select onValueChange={onInstrumentSelect}>
              <SelectTrigger>
                <SelectValue placeholder={t('selectInstrument')} />
              </SelectTrigger>
              <SelectContent>
                {allAvailableInstruments.map((inst) => (
                  <SelectItem key={inst} value={inst}>
                    {t(inst as any) || inst}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <div className="text-center">
              <Button 
                variant="outline" 
                onClick={() => setShowCustomInput(true)}
                className="w-full"
              >
                {t('createCustomInstrument')}
              </Button>
            </div>
          </>
        ) : (
          <div className="space-y-3">
            <Input
              value={customInstrument}
              onChange={(e) => setCustomInstrument(e.target.value)}
              placeholder={t('instrumentPlaceholder')}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  handleCustomInstrumentSubmit();
                }
              }}
              autoFocus
            />
            <div className="flex gap-2">
              <Button 
                onClick={handleCustomInstrumentSubmit}
                disabled={!customInstrument.trim()}
                className="flex-1"
              >
                {t('confirm')}
              </Button>
              <Button 
                variant="outline" 
                onClick={() => {
                  setShowCustomInput(false);
                  setCustomInstrument("");
                }}
                className="flex-1"
              >
                {t('cancel')}
              </Button>
            </div>
          </div>
        )}
      </div>
    );
  }

  // Combinar todos los instrumentos (predefinidos + personalizados)
  const allInstruments = [...INSTRUMENTS, ...customInstruments.filter(ci => !INSTRUMENTS.includes(ci as any))];
  const otherInstruments = allInstruments.filter(inst => inst !== currentInstrument);

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-lg font-light mb-2">{t('yourInstrument')}: {t(currentInstrument as any) || currentInstrument}</h2>
        <Button variant="outline" onClick={() => onInstrumentSelect("")}>
          {t('change')}
        </Button>
      </div>

      <div className="space-y-4">
        <div className="grid grid-cols-2 sm:grid-cols-3 gap-2">
          {/* Mostrar primero el instrumento actual */}
          <button
            onClick={() => setTargetInstrument(currentInstrument)}
            className={`p-3 rounded-lg transition-all h-16 flex items-center justify-center text-center text-sm font-semibold ${
              targetInstrument === currentInstrument 
                ? 'ring-2 ring-offset-2 ring-black dark:ring-white scale-105' 
                : 'hover:scale-105'
            }`}
            style={{
              backgroundColor: getInstrumentColor(currentInstrument).bg,
              color: getInstrumentColor(currentInstrument).text
            }}
          >
            <span className="truncate px-1">{t('me')} ({t(currentInstrument as any) || currentInstrument})</span>
          </button>
          {/* Luego mostrar el resto de instrumentos */}
          {otherInstruments.map((inst) => (
            <button
              key={inst}
              onClick={() => setTargetInstrument(inst)}
              className={`p-3 rounded-lg transition-all h-16 flex items-center justify-center text-center text-sm font-semibold ${
                targetInstrument === inst 
                  ? 'ring-2 ring-offset-2 ring-black dark:ring-white scale-105' 
                  : 'hover:scale-105'
              }`}
              style={{
                backgroundColor: getInstrumentColor(inst).bg,
                color: getInstrumentColor(inst).text
              }}
            >
              <span className="truncate px-1">{t(inst as any) || inst}</span>
            </button>
          ))}
        </div>

        {targetInstrument && (
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              {/* Controles de Volumen - Bajar a la izquierda, Subir a la derecha */}
              <Button
                className="h-16 text-xl font-light"
                onClick={() => handleRequest({ targetInstrument, action: "volume_down" })}
                disabled={loading}
                variant="secondary"
              >
                {loading ? t('sending') : t('volumeDown')}
              </Button>
              <Button
                className="h-16 text-xl font-light"
                onClick={() => handleRequest({ targetInstrument, action: "volume_up" })}
                disabled={loading}
              >
                {loading ? t('sending') : t('volumeUp')}
              </Button>

              {/* Controles de Reverb */}
              <Button
                className="h-16 text-xl font-light"
                onClick={() => handleRequest({ targetInstrument, action: "reverb_down" })}
                disabled={loading}
                variant="secondary"
              >
                {loading ? t('sending') : t('reverbDown')}
              </Button>
              <Button
                className="h-16 text-xl font-light"
                onClick={() => handleRequest({ targetInstrument, action: "reverb_up" })}
                disabled={loading}
              >
                {loading ? t('sending') : t('reverbUp')}
              </Button>
            </div>

            {/* Botones especiales */}
            <div className="grid grid-cols-2 gap-4 pt-2">
              <Button
                className="h-12 text-sm font-light bg-green-500 hover:bg-green-600 text-white"
                onClick={() => handleRequest({ targetInstrument: currentInstrument, action: "thanks" })}
                disabled={loading}
              >
                {loading ? t('sending') : t('thanks')}
              </Button>
              <Button
                className="h-12 text-sm font-light bg-red-500 hover:bg-red-600 text-white"
                onClick={() => handleRequest({ targetInstrument: currentInstrument, action: "assistance" })}
                disabled={loading}
              >
                {loading ? t('sending') : t('assistance')}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/RequestQueue.tsx.content:
import { useState } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/hooks/use-language";
import { INSTRUMENT_COLORS } from "@/lib/constants";

interface Request {
  id: number;
  musician: string;
  targetInstrument: string;
  action: string;
  completed: boolean;
}

interface Props {
  requests: Request[];
  roomId: string;
}

export default function RequestQueue({ requests, roomId }: Props) {
  const [completedIds, setCompletedIds] = useState<number[]>([]);
  const { toast } = useToast();
  const { t } = useLanguage();

  const handleComplete = async (requestId: number) => {
    try {
      const response = await fetch(`/api/rooms/${roomId}/requests/${requestId}/complete`, {
        method: 'POST'
      });

      if (!response.ok) {
        throw new Error('Error al completar la petición');
      }

      // Ya no necesitamos rastrear IDs localmente porque el WebSocket actualizará el estado
      toast({
        title: t('requestCompleted'),
        description: t('requestCompletedDesc')
      });
    } catch (error) {
      console.error('Error completando petición:', error);
      toast({
        title: t('error'),
        description: t('errorCompletingRequest'),
        variant: "destructive"
      });
    }
  };

  // Ahora solo filtramos las peticiones completadas según la propiedad completed
  // El WebSocket ya filtra las completadas, pero por seguridad lo hacemos aquí también
  const activeRequests = requests.filter(r => !r.completed);

  // Invertir el orden para mostrar las más recientes arriba
  const sortedRequests = [...activeRequests].reverse();

  return (
    <div className="space-y-4">
      {sortedRequests.length > 0 ? (
        sortedRequests.map((request) => (
          <Card 
            key={request.id} 
            className="p-4 hover:shadow-md transition-shadow"
            style={{
              backgroundColor: INSTRUMENT_COLORS[request.musician]?.bg + '40',
              borderColor: INSTRUMENT_COLORS[request.musician]?.bg
            }}
          >
            <div className="flex justify-between items-center">
              <div>
                <p 
                  className="text-2xl font-bold uppercase" 
                  style={{ color: INSTRUMENT_COLORS[request.musician]?.text }}
                >
                  {t(request.musician as any) || request.musician}
                </p>
                {request.action === 'thanks' ? (
                  <p className="text-xl font-light" style={{ color: '#22c55e' }}>
                    {t('thanksMessage')}
                  </p>
                ) : request.action === 'assistance' ? (
                  <p className="text-xl font-light" style={{ color: '#ef4444' }}>
                    {t('assistanceMessage')}
                  </p>
                ) : (
                  <p className="text-xl font-semibold uppercase">
                    <span style={{ color: INSTRUMENT_COLORS[request.targetInstrument]?.text }}>
                      {t(request.targetInstrument as any) || request.targetInstrument}
                    </span>{' '}
                    <span style={{ color: request.action.includes('up') ? '#ff0000' : '#00ff00' }}>
                      {request.action.includes('up') ? '+' : '-'}
                    </span>{' '}
                    <span style={{ 
                      color: request.action.includes('up') ? '#ff0000' : '#00ff00'
                    }}>
                      {request.action.includes('reverb') ? 'REVERB' : 'VOLUMEN'}
                    </span>
                  </p>
                )}
              </div>
              <Button 
                onClick={() => handleComplete(request.id)}
                variant="secondary"
                className="hover:bg-green-100"
              >
                {t('completed')}
              </Button>
            </div>
          </Card>
        ))
      ) : (
        <p className="text-center text-gray-500">{t('noPendingRequests')}</p>
      )}
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

